import logging
from PyQt5.QtWidgets import QMessageBox



def configLogs(logger_name, log_file, use_qt_dialogs=False):
    # Configure the logger with the given logger name and log file
    logger = logging.getLogger(logger_name)
    logger.setLevel(logging.DEBUG)

    # Set the log message format
    formatter = logging.Formatter('%(asctime)s - %(name)s - %(levelname)s - %(message)s')

    # Save logs to a file
    file_handler = logging.FileHandler(log_file)
    file_handler.setFormatter(formatter)
    logger.addHandler(file_handler)

    if use_qt_dialogs:
        # Display logs in QMessageBox dialogs
        qt_handler = QtHandler()
        qt_handler.setFormatter(formatter)
        logger.addHandler(qt_handler)
    else:
        # Display logs in the console
        console_handler = logging.StreamHandler()
        console_handler.setFormatter(formatter)
        logger.addHandler(console_handler)

    return logger

#Now we will use the QMessageBox for display a visual representation
class QtHandler(logging.Handler):
    def __init__(self, *args, **kwargs):
        super(QtHandler, self).__init__(*args, **kwargs)

    def emit(self, record):
        msg = self.format(record)
        level = record.levelno
        if level >= logging.ERROR:
            QMessageBox.critical(None, "Error", msg)
        elif level >= logging.WARNING:
            QMessageBox.warning(None, "Warning", msg)
        elif level >= logging.INFO:
            QMessageBox.information(None, "Info", msg)
        else:
            QMessageBox.information(None, "Debug", msg)




